mapper.select:
SELECT * FROM us_role WHERE id in (
    SELECT us_role_id FROM us_user_role WHERE us_user_id = #{id}
    )

        SELECT * FROM us_resource WHERE id in (
            SELECT us_resource_id FROM us_role_resource WHERE us_role_id in (
                SELECT id FROM us_role WHERE id in (
                    SELECT us_role_id FROM us_user_role WHERE us_user_id = #{userId}
                    )
                )
            )
order by sort Asc
